package kiymet.week3;public class ReverseNegativeNumber {        public static int reverseNegative(int num) {            String numStr = Integer.toString(Math.abs(num));            StringBuilder reversedStr = new StringBuilder(numStr).reverse();            int reversedNum = Integer.parseInt(reversedStr.toString());            return num < 0 ? -reversedNum : reversedNum;        }        public static void main(String[] args) {            int negativeNum = -12345;            int reversedNegativeNum = reverseNegative(negativeNum);            System.out.println("Original negative number: " + negativeNum);            System.out.println("Reversed negative number: " + reversedNegativeNum);        }    }